  `MoTa` varchar(4000) NOT NULL DEFAULT 'Chưa có mô tả',
  `NgayCapNhat` date NOT NULL DEFAULT '2017-01-12',
  `GiaBan` float UNSIGNED NOT NULL DEFAULT 0,
  `GiaKhuyenmai` int(11) UNSIGNED NOT NULL DEFAULT 0,
  `urlHinh` varchar(255) NOT NULL,
  `baiviet` text DEFAULT NULL,
  `SoLanXem` int(4) DEFAULT 0,
  `SoLuongTonKho` int(4) UNSIGNED DEFAULT 0,
  `GhiChu` varchar(255) DEFAULT NULL,
  `SoLanMua` int(4) DEFAULT 0,
  `AnHien` int(1) DEFAULT 1
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `sanpham`
--

INSERT INTO `sanpham` (`idSP`, `idNH`, `idL`, `TenSP`, `MoTa`, `NgayCapNhat`, `GiaBan`, `GiaKhuyenmai`, `urlHinh`, `baiviet`, `SoLanXem`, `SoLuongTonKho`, `GhiChu`, `SoLanMua`, `AnHien`) VALUES
(1, 1, 2, 'Nước hoa Flower by Kenzo EDP 100ml', '<p><a href=\"https://vuahanghieu.com/nuoc-hoa-flower-by-kenzo-edp-100ml-ph007024\"><strong>Nước hoa Flower by Kenzo EDP 100ml</strong></a>&nbsp;l&agrave; chai nước hoa Ph&aacute;p nữ được giới thiệu v&agrave;o năm 2000 thuộc&nbsp;<a href=\"https://vuahanghieu.com/nuoc-hoa/oriental-floral-hoa-co-phuong-dong\">nh&oacute;m hương hoa cỏ phương Đ&ocirc;ng</a>.&nbsp;<a href=\"https://vuahanghieu.com/nuoc-hoa/floral-tong-hoa\">Hương hoa</a>&nbsp;n&agrave;y được x&acirc;y dựng tr&ecirc;n 3 loại hương cơ bản l&agrave; hoa hồng, hoa nh&agrave;i v&agrave; xạ hương.</p>\r\n', '2021-05-12', 1500000, 1150000, 'Nước hoa Flower by Kenzo EDP 100ml.jpg', NULL, 0, 0, '', 0, 0),
(2, 18, 2, 'Calvin Klein Euphoria Blossom Women', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-calvin-klein-117-83-0-/2/1\">Nước hoa Calvin Klein</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Mỹ &amp; Ph&aacute;p</p>\r\n\r\n<p>M&ugrave;i hương đặc trưng</p>\r\n\r\n<ul>\r\n	<li>Hương đầu: quả lựu, quả quất v&agrave;ng</li>\r\n	<li>Hương giữa: hoa phong lan, hoa mẫu đơn hồng, hoa sen</li>\r\n	<li>Hương cuối: xạ hương, hương gỗ, hổ ph&aacute;ch trắng</li>\r\n</ul>\r\n\r\n<p>Phong c&aacute;ch: thanh lịch, tinh tế, nữ t&iacute;nh</p>\r\n\r\n<p>Lấy cảm hứng từ m&ugrave;i hương tinh tế của một nụ hoa đang h&eacute; nở,&nbsp;<strong>Euphoria Blossom</strong>&nbsp;mang m&ugrave;i hương thảo dược tươi m&aacute;t v&agrave; nồng n&agrave;n, đ&aacute;nh thức mọi gi&aacute;c quan với hương thơm quyến rũ đầy huyền b&iacute;.</p>\r\n', '2021-05-01', 1755000, 1450000, 'Calvin Klein Endless Euphoria Women 125ml_2.PNG', NULL, 20, 50, '', 10, 1),
(3, 18, 2, 'Calvin Klein Eternity For Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-calvin-klein-117-83-0-/2/1\">Nước hoa Calvin Klein</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Mỹ &amp; Ph&aacute;p</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương hoa cỏ tr&aacute;i c&acirc;y</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;Tạm ổn - 3 giờ đến 6 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Quyến rũ, thu h&uacute;t, nữ t&iacute;nh</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;Quả qu&yacute;t hồng, Cam Bergamot, Hoa anh đ&agrave;o</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Hoa hồng, Hoa t&iacute;m, Hoa tử đinh hương trắng</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Xạ hương, Tre, Gỗ đ&agrave;n hương</p>\r\n\r\n<p><strong>Tổng quan:</strong>&nbsp;Th&iacute;ch hợp d&ugrave;ng cho ban ng&agrave;y, v&agrave;o m&ugrave;a xu&acirc;n v&igrave; hương thơm đầy tươi tắn, tự nhi&ecirc;n chứa đựng b&ecirc;n trong n&oacute;. Chỉ cần phối hợp với một bộ c&aacute;nh th&iacute;ch hợp, th&igrave; hương nước hoa n&agrave;y chắc chắn sẽ biến bạn th&agrave;nh một thỏi nam ch&acirc;m thu h&uacute;t những &aacute;nh nh&igrave;n ngưỡng mộ cũng như những lời khen kh&ocirc;ng ngớt từ mọi người.</p>\r\n', '2021-05-01', 1250000, 0, 'Calvin-Klein-Eternity-Men-1.jpg', NULL, 7, 10, '', 6, 1),
(4, 10, 1, 'Chanel Bleu De Chanel Eau De Parfum Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-chanel-120-82-0-/2/1\">Nước hoa Chanel</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương gỗ thơm</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;Rất l&acirc;u - Tr&ecirc;n 12 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n, Hạ, Thu, Đ&ocirc;ng</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Mạnh mẽ, nam t&iacute;nh, quyến rũ</p>\r\n\r\n<p><strong>Hương ch&iacute;nh:</strong>&nbsp;Hương Labdanum, Nhục đậu khấu, Gừng, C&acirc;y hoắc hương, Gỗ đ&agrave;n hương, Cỏ hương b&agrave;i, Hương gỗ, C&acirc;y bạc h&agrave;,&nbsp;Hoa nh&agrave;i, Quả bưởi,&nbsp;Quả chanh v&agrave;ng, Nhang (Hương), Hồng ti&ecirc;u,&nbsp;Gỗ tuyết t&ugrave;ng, Hổ ph&aacute;ch.</p>\r\n\r\n<p><strong>Tổng quan:</strong>&nbsp;<strong><a href=\"http://perfume168.com/nuoc-hoa/12321chanel-bleu-de-chanel-eau-de-parfum-1095\">Bleu de Chanel</a>&nbsp;Eau De Parfum</strong>&nbsp;được đ&aacute;nh gi&aacute; l&agrave; một m&ugrave;i hương nam t&iacute;nh cho ph&aacute;i mạnh với kh&aacute; nhiều điểm tốt. Với độ lưu hương l&acirc;u v&agrave; độ tỏa hương vừa phải, đ&acirc;y l&agrave; một lựa chọn tuyệt vời để sử dụng mỗi ng&agrave;y trong tất cả c&aacute;c m&ugrave;a trong năm. Ngo&agrave;i ra, m&ugrave;i hương n&agrave;y c&oacute; thể sử dụng được trong nhiều trường hợp, từ đi l&agrave;m đến d&ugrave;ng trong l&uacute;c đi chơi, dạo phố hay clubbing đều tuyệt vời.</p>\r\n', '2021-05-01', 3835000, 3050000, 'Chanel Bleu De Chanel Eau De Parfum Men_1.PNG', NULL, 10, 8, '', 5, 1),
(5, 10, 1, 'Chanel Allure Homme Sport Eau Extreme Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-chanel-120-82-0-/2/1\">Nước hoa Chanel</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương gỗ thơm</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;L&acirc;u - 7 giờ đến 12 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Xa - Toả hương trong v&ograve;ng b&aacute;n k&iacute;nh 2 m&eacute;t</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n, Hạ, Thu</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;HIện đại, nam t&iacute;nh, mạnh mẽ</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;C&acirc;y x&ocirc; thơm, C&acirc;y bạc h&agrave;, Quả qu&yacute;t hồng,&nbsp;C&acirc;y b&aacute;ch</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Ti&ecirc;u</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Xạ hương, Gỗ tuyết t&ugrave;ng, Gỗ đ&agrave;n hương,&nbsp;Đậu Tonka</p>\r\n\r\n<p><strong>Tổng quan:</strong>&nbsp;Một loại hương đa dụng, c&oacute; thể sử dụng tốt cả ng&agrave;y lẫn đ&ecirc;m v&agrave; v&agrave;o tất cả c&aacute;c m&ugrave;a trong năm, đặc biệt l&agrave; m&ugrave;a xu&acirc;n, Th&iacute;ch hợp với những chuyến du xu&acirc;n, hoặc đi ăn trưa c&ugrave;ng bạn b&egrave;.</p>\r\n', '2021-05-01', 2855000, 3050000, 'Chanel Allure Homme Sport Eau Extreme Men_1.PNG', NULL, 10, 14, '', 5, 1),
(7, 11, 1, 'Dior Eau Sauvage Parfum Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-dior-134-82-0-/2/1\">Nước hoa Dior</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n', '2021-05-06', 1650000, 0, 'Dior Eau Sauvage Parfum Men_1.PNG', NULL, 0, 20, '', 0, 0),
(8, 11, 1, 'Dior Eau Sauvage Men(Tester)', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-dior-134-82-0-/2/1\">Nước hoa Dior</a></p>\r\n', '2021-05-01', 1650000, 0, 'Dior Eau Sauvage Men(Tester)_1.PNG', NULL, 0, 11, '', 0, 1),
(9, 6, 3, 'Diptyque Tam Dao Eau De Parfum', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-diptyque-1145-86-0-/2/1\">Nước Hoa Diptyque</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương hoa cỏ Gỗ - Xạ hương</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;Tạm ổn - 3 giờ đến 6 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Thu, Đ&ocirc;ng</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Quyến rũ, gợi cảm, tinh tế</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;Hoa hồng, Hoa sim, C&acirc;y b&aacute;ch &Yacute;</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Gỗ đ&agrave;n hương, Gỗ tuyết t&ugrave;ng</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Gia vị, Hổ ph&aacute;ch, Xạ hương trắng, Gỗ cẩm lai Brazil</p>\r\n\r\n<p><strong>Tổng quan:</strong>&nbsp;Đ&acirc;y l&agrave; một hương thơm kh&aacute; đa dụng, c&oacute; thể sử dụng tốt cho cả ng&agrave;y lẫn đ&ecirc;m, Độ tỏa hương l&yacute; tưởng cho hai m&ugrave;a thu, đ&ocirc;ng v&agrave; đ&ocirc;i khi l&agrave; m&ugrave;a xu&acirc;n. Th&iacute;ch hợp kể cả khi đi l&agrave;m hoặc đi chơi.</p>\r\n', '2021-05-01', 3650000, 0, 'Diptyque Tam Dao Eau De Parfum_1.PNG', NULL, 0, 5, '', 13, 1),
(10, 19, 2, 'Elie Saab Le Parfum Women', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-elie-saab-138-83-0-/2/1\">Nước hoa Elie Saab</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n\r\n<p>M&ugrave;i hương đặc trưng:</p>\r\n\r\n<ol>\r\n	<li>Hương đầu: hoa cam</li>\r\n	<li>Hương giữa: hoa nh&agrave;i</li>\r\n	<li>Hương cuối: hoắc hương, tuyết t&ugrave;ng, hoa hồng, mật ong</li>\r\n</ol>\r\n\r\n<p>Nh&oacute;m hương: Floral<br />\r\nPhong c&aacute;ch: Nữ t&iacute;nh, dịu d&agrave;ng, thuần khiết</p>\r\n\r\n<p>Hương thơm đầu ti&ecirc;n của thương hiệu thời trang cao cấp Lebanon mang t&ecirc;n gọi&nbsp;<strong><a href=\"http://perfume168.com/nuoc-hoa/elie-saab-le-parfum-women-2056\">Elie Saab Le Parfum</a>.&nbsp;</strong>C&aacute;c th&agrave;nh phần từ&nbsp;hoa cam, hoa hồng v&agrave; mật ong ch&iacute;nh l&agrave; hạt nh&acirc;n đ&atilde; tạo n&ecirc;n m&ugrave;i hương dịu d&agrave;ng, lắng đọng cho chai nước hoa n&agrave;y.<strong>&nbsp;</strong><strong>Le Parfum</strong>&nbsp;được nh&agrave; sản xuất c&ocirc;ng bố l&agrave; cực kỳ gợi cảm v&agrave; duy&ecirc;n d&aacute;ng.&nbsp;L&agrave;n hương mở đầu&nbsp;l&agrave; sự thơm m&aacute;t của hoa cam, kế đ&ecirc;n l&agrave; hương nh&agrave;i quyến rũ. Kết th&uacute;c đầy đam m&ecirc; v&agrave; thuần khiết với sự c&oacute; mặt&nbsp;hoa hồng, mật ong ngọt ng&agrave;o hương thơm nồng ấm của của gỗ tuyết t&ugrave;ng v&agrave; hoắc hương.</p>\r\n', '2021-05-01', 1850000, 0, 'Elie Saab Le Parfum Women_1.PNG', NULL, 0, 5, '', 0, 1),
(11, 19, 2, 'Elie Saab Rose Couture', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-elie-saab-138-83-0-/2/1\">Nước hoa Elie Saab</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Ph&aacute;p</p>\r\n', '2021-05-01', 2650000, 0, 'Elie Saab Rose Couture_1.PNG', NULL, 0, 40, '', 0, 1),
(12, 12, 1, 'Scuderia Ferrari Racing Red Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-ferrari-145-82-0-/2/1\">Nước hoa Ferrari</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Italy</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa: Hương gỗ</strong></p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;Rất l&acirc;u - Tr&ecirc;n 8 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n, Thu</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Mạnh mẽ, c&aacute; t&iacute;nh</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;Quả chanh xanh, Ti&ecirc;u, Ngải.</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Hoa Oải Hương, C&acirc;y đơn s&acirc;m, Hương kim loại.</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Gỗ tuyết t&ugrave;ng, Đậu Tonka, Da thuộc.</p>\r\n\r\n<p><a href=\"http://perfume168.com/nuoc-hoa-nam-182-82-0-/0/1\">Perfume168 - Nước hoa nam Scuderia Ferrari Racing Red</a>&nbsp;</p>\r\n\r\n<p>Scuderia Ferrari Racing Red bởi&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-ferrari-145-82-0-/2/1\">Ferrari</a>&nbsp;với hương thơm gỗ (Woody) đặt trưng cho nam giới. Scuderia Ferrari Racing Red được đưa ra v&agrave;o năm 2013.</p>\r\n', '2021-05-01', 1250000, 0, 'Scuderia Ferrari Racing Red Men_1.PNG', NULL, 0, 8, '', 1, 1),
(13, 12, 1, 'Ferrari Black Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-ferrari-145-82-0-/2/1\">Nước hoa Ferrari</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Italy</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương thơm dương xỉ</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;Tạm ổn - 3 giờ đến 6 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n, Thu</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Nam t&iacute;nh, thể thao, lịch l&atilde;m</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;Quả mận, Quả chanh xanh, Cam Bergamot, T&aacute;o đỏ.</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Quế, Hoa nh&agrave;i, Hoa hồng, Bạch đậu khấu.</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Hổ ph&aacute;ch, Hương Va ni, Xạ hương, Gỗ tuyết t&ugrave;ng</p>\r\n\r\n<p><strong>Tổng quan:</strong>&nbsp;Th&iacute;ch hợp d&ugrave;ng cho cả ban ng&agrave;y v&agrave; ban đ&ecirc;m, v&agrave;o m&ugrave;a xu&acirc;n, thu. Hương thơm chắc chăn sẽ thu h&uacute;t được sự ch&uacute; &yacute; cho d&ugrave; bạn đang ở đ&acirc;u.</p>\r\n', '2021-05-01', 1250000, 0, 'Ferrari Black Men_1.PNG', NULL, 0, 15, '', 0, 1),
(14, 12, 1, 'Ferrari Uomo Men', '<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Nh&atilde;n hiệu:&nbsp;<a href=\"http://perfume168.com/nuoc-hoa-ferrari-145-82-0-/2/1\">Nước hoa Ferrari</a></p>\r\n\r\n<p><img src=\"http://perfume168.com/Images/ImageSource/checkicon.jpg\" />&nbsp;Xuất xứ: Italy</p>\r\n\r\n<p><strong>Hương đầu:</strong>&nbsp;Quả chanh v&agrave;ng Amalfi, Cam Bergamot, C&acirc;y b&aacute;ch x&ugrave;.</p>\r\n\r\n<p><strong>Hương giữa:</strong>&nbsp;Da thuộc, Nhục đậu khấu, Gỗ tuyết t&ugrave;ng Virginia.</p>\r\n\r\n<p><strong>Hương cuối:</strong>&nbsp;Hương Labdanum Ph&aacute;p, Đậu Tonka, C&acirc;y hoắc hương.</p>\r\n\r\n<p><strong>Nh&oacute;m nước hoa:</strong>&nbsp;Hương gỗ cay nồng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Độ tuổi khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;Tr&ecirc;n 25</p>\r\n\r\n<p><strong>Năm ra mắt:</strong>&nbsp;2009</p>\r\n\r\n<p><strong>Nồng độ:</strong>&nbsp;EDT</p>\r\n\r\n<p><strong>Độ lưu hương:</strong>&nbsp;hơn 6 giờ</p>\r\n\r\n<p><strong>Độ toả hương:</strong>&nbsp;Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay</p>\r\n\r\n<p><strong>Thời điểm khuy&ecirc;n d&ugrave;ng:</strong>&nbsp;kh&ocirc;ng giới hạn</p>\r\n\r\n<p><strong>Phong c&aacute;ch:</strong>&nbsp;Nam t&iacute;nh, lịch l&atilde;m</p>\r\n', '2021-04-30', 1250000, 0, 'Ferrari Uomo Men_1.PNG', NULL, 0, 30, '', 0, 1),
